#include <stdint.h>
#include <stdio.h>

int main(void)
{
	printf("Hello World\n");
	// This can be modified at any time (string variable)
	char msg1[] = "Hello how are you?";

	// Always add const for a string literal. This is stored on the stack in RAM but points to ROM and therefore will not change even with no compiler error
	char *msg2 = "fastbitlab.com";

	msg2[0] = 'b'; // This shouldn't change the variable since it is stored in ROM

	printf("Message is: %s\n", msg1);
	printf("Message is: %s\n", msg2);
	printf("Address of msg2 is: %p\n", &msg2);
	printf("Value of msg2 is: %p\n", msg2);

    /* Loop forever */
	for(;;);
}
